function renderTodos(t){$(".todo-list ul").empty(),t.map(t=>{null!==t.id&&$(`\n      <li>\n        <input data-todoid=${t.id} id="check" type="checkbox" \n        ${t.isDone&&"checked"}/>\n        <p>${t.content}</p><span data-todoid=${t.id} class="btn-delete">Delete</span><span data-todoid=${t.id} class="btn-update">Update</span>\n      </li>\n      `).appendTo(".todo-list ul")})}$((function(){const t=JSON.parse(localStorage.getItem("todos"))||[{id:null,content:"",isDone:!1}];let e=null;renderTodos(t),$(".add-todo").click((function(o){o.preventDefault();let n=$("input.todo").val();if(n){if(e){e.content=n;let o=t.findIndex(t=>t.id===e.id);t.splice(o,1,e),localStorage.setItem("todos",JSON.stringify(t)),e=null}else t.unshift({id:+Math.random().toFixed(2),content:n,isDone:!1});renderTodos(t)}else alert("Input is empty");$("input.todo").val(""),localStorage.setItem("todos",JSON.stringify(t))})),$(".todo-list").on("click",".btn-delete",(function(e){e.preventDefault();let o=$(this).data("todoid"),n=t.findIndex(t=>t.id===o);t.splice(n,1),localStorage.setItem("todos",JSON.stringify(t)),renderTodos(t)})),$(".todo-list").on("click",".btn-update",(function(o){o.preventDefault();let n=$(this).data("todoid");e=t.find(t=>t.id===n),$("input.todo").val(e.content),localStorage.setItem("todos",JSON.stringify(t))})),$(".todo-list").on("click","#check",(function(e){e.preventDefault();let o=$(this).data("todoid"),n=t.findIndex(t=>t.id===o);t[n].isDone=!t[n].isDone,renderTodos(t)}))}));